mapper.select:
WITH InputSymptoms AS (
SELECT #{symptom} AS symptom_name FROM DUAL
), InputCount AS ( SELECT COUNT(*) AS total_input FROM InputSymptoms ), MatchedDiseases AS ( SELECT dsm.DISEASE_NAME, COUNT(DISTINCT dsm.SYMPTOM_NAME) AS matched_count FROM DISEASE_SYMPTOM_MAPPINGS dsm JOIN InputSymptoms inp ON dsm.SYMPTOM_NAME = inp.symptom_name GROUP BY dsm.DISEASE_NAME ), AllSymptoms AS ( SELECT DISEASE_NAME, LISTAGG(SYMPTOM_NAME, ', ') WITHIN GROUP (ORDER BY SYMPTOM_NAME) AS all_symptoms FROM ( SELECT DISTINCT DISEASE_NAME, SYMPTOM_NAME FROM DISEASE_SYMPTOM_MAPPINGS ) GROUP BY DISEASE_NAME ) SELECT md.DISEASE_NAME AS "질환명", LISTAGG(dd.DEPARTMENT_NAME, ', ') WITHIN GROUP (ORDER BY dd.DEPARTMENT_NAME) AS "진료과", asym.all_symptoms AS "전체증상목록", d.IMAGE_URL AS "이미지", d.DESCRIPTION AS "설명", d.TOTAL_PATIENTS AS "환자수" FROM MatchedDiseases md CROSS JOIN InputCount ic JOIN DISEASES d ON md.DISEASE_NAME = d.DISEASE_NAME LEFT JOIN DISEASE_DEPARTMENTS dd ON d.DISEASE_NAME = dd.DISEASE_NAME LEFT JOIN AllSymptoms asym ON d.DISEASE_NAME = asym.DISEASE_NAME WHERE md.matched_count >= 1 GROUP BY md.DISEASE_NAME, md.matched_count, d.total_patients, d.IMAGE_URL, d.DESCRIPTION, asym.all_symptoms ORDER BY md.matched_count DESC, d.total_patients DESC NULLS LAST FETCH FIRST 20 ROWS ONLY

mapper.insert:
INSERT INTO FEATURED_DISEASES (
FEATURED_DISEASES_NO,
DISEASE_NAME,
DISPLAY_ORDER
) VALUES (
FEATURED_DISEASES_SEQ.NEXTVAL,
#{diseaseName},
#{displayOrder}
)

update.foreach:
UPDATE FEATURED_DISEASES
SET DISPLAY_ORDER = #{item.displayOrder}
WHERE FEATURED_DISEASES_NO = #{item.featuredDiseasesNo}
